INSERT INTO `hr_kpi_list` (`KPI_LIST_ID`, `COMPANY_ID`, `KPI_TITLE`, `DESCRIPTION`, `VALID_FROM`, `VALID_TO`, `INPUT_TYPE`, `PARENT_ID`, `KORELASI`,`RANGE_START`,`RANGE_END`,`NUMBER_INDEX`) VALUES ('20220417224', '504404575327187914', 'Persentase pemenuhan penguatan pengelolaan sistem, kebijakan dan prosedur pengendalian intern (CMS)', 'Penguatan dan pengelolaan otomasi sistem, kebijakan dan prosedur pengendalian intern (CMS) berkolaborasi dengan satker terkait secara komprehensif dan terintegrasi. Pengukuran KPI : Tingkat kepuasan atasan penilai/satker terkait/organisasi, didasarkan atas pencapaian tahapan kemajuan kegiatan pengembangan (enhancement)  sistem, yang dipantau dan dinilai berkala (1/3/6 blnan). Perlu penetapan skor, parameter progress project dan kriteria tingkat kepuasan. Polarisasi KPI : maximize (semakin tinggi tingkat kepuasan semakin baik nilai kinerja satker)', '2022-03-01 00:00:00', '9999-12-31 00:00:00', '0', '0', '0','0', '0','224'); 
